top.js:
function fmt(s) {
    if (s > 0.1 * 1024 * 1024 * 1024) {
      var f = s / 1024 / 1024 / 1024;
      return f.toFixed(1) + ' GB';
    } else if (s > 0.1 * 1024 * 1024) {
      var f = s / 1024 / 1024;
      return f.toFixed(1) + 'MB';
    } else {
      var f = s / 1024;
      return f.toFixed(1) + 'kb';
    }
};
var jj = JSON.parse(torrentclient.json());
var html = '';
var list = jj.MovieList;
for(var index in list) {
  var item = list[index];
  html += '<div class="movie">\n';
  html += '  <h3 align="center" class="title">' + item.title + '</h3>\n';
  html += '  <div align="center"><img class="poster" src="' + item.poster_med.replace('https:', 'http:') + '" /></div>\n';
  html += '  <div align="center" class="genres">';
  var genres = item.genres;
  for(var index in genres) {
    var genre = genres[index].trim();
    if (genre.length > 0) {
      html += '<b>' + genre + ', </b>';
    }
  }
  html += 'imdb: <a href="http://www.imdb.com/title/' + item.imdb + '">' + item.rating + '/10</a> ';
  html += '</div>';
  html += '  <p align="justify" class="description">';
  html += '<b>' + item.year + '</b> ';
  html += item.description + '</p>\n';
  var items = item.items;
  items.sort(function(a,b) {
    return b.torrent_seeds-a.torrent_seeds;
  });
  for(var index in items) {
    var source = items[index];
    html += '  <p>\n';
    html += '    <b class="quality">' + source.quality + '</b>\n';
    html += '    / <span class="size">' + fmt(source.size_bytes) + '</span>\n';
    html += '    / seed:<span class="seed">' + source.torrent_seeds + '</span>\n';
    html += '    / <a class="magnet" href="' + encodeURI(source.torrent_magnet) + '">magnet</a>\n';
    html += '    / <a class="torrent" href="' + encodeURI(source.torrent_url) + '">torrent</a>\n';
    html += '  </p>\n';
  };
  html += '</div>\n';
};
if(list.length > 0) {
  page = (RegExp('page' + '=' + '(.+?)(&|$)').exec(location.href)||[,null])[1]
  newpage = 'page=' + (parseInt(page,10)+1);
  href = location.href.replace(/page=[^&]+/, newpage);
  html += '<a class="next" href="' + href + '" /a>'
}
document.body.innerHTML=html;

